            JAXBContext jc = JAXBContext.newInstance( "ErpinventoryWs.jaxb" ); 
             
            // create an Unmarshaller 
            Unmarshaller u = jc.createUnmarshaller(); 
             
            // unmarshal a (ErpinventoryWs) instance document into a tree of Java content 
            // objects composed of classes from package. 
            JAXBElement<?> wsElement = (JAXBElement<?>)u.unmarshal( new FileInputStream( "ErpinventoryWs.xml" ) ); 
 
		System.out.println("JAXBElement Name / Declared Type : "+ wsElement.getName() +" / "+ wsElement.getDeclaredType() );  
 
		ErpinventoryWs wsVar = (ErpinventoryWs) wsElement.getValue(); 
 
 
		ErpinventoryForm wsForm1 = (ErpinventoryForm) wsVar.getErpinventoryForm(); 
